// Type definitions for caseless 0.12
// Project: https://github.com/mikeal/caseless
// Definitions by: downace <https://github.com/downace>
//                 Matt R. Wilson <https://github.com/mastermatt>
// Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped
// TypeScript Version: 2.2

type KeyType = string;
type ValueType = any;
type RawDict = object;

export interface Caseless {
    set(description: KeyType, value: ValueType, clobber?: boolean): KeyType | false;
    set(dict: RawDict): void;
    has(description: KeyType): KeyType | false;
    get(description: KeyType): ValueType | undefined;
    swap(description: KeyType): void;
    del(description: KeyType): boolean;
}

export interface Httpified {
  headers: RawDict;
  setHeader(description: KeyType, value: ValueType, clobber?: boolean): KeyType | false;
  setHeader(dict: RawDict): void;
  hasHeader(description: KeyType): KeyType | false;
  getHeader(description: KeyType): ValueType | undefined;
  removeHeader(description: KeyType): boolean;
}

export function httpify(resp: object, headers: RawDict): Caseless;

declare function caseless(dict?: RawDict): Caseless;

export default caseless;
